import React, { useState } from "react";
const App=()=>{
  const state=useState();
  const[name,setname]=useState("");
  const[list,setlist]=useState([]);
  const inputevent=(event)=>{
    setname(event.target.value);
  }
  const deltodolist = (index) => {
    console.log('deleted');
  var newlist=list;
  newlist.slice(index,1);
  setlist(newlist);
  }
  const todolist=()=>{
    setlist((prev)=>{
      return[...prev,name];
    });
    setname("");
  }
  return(<><div className="main">
    <div className="center"><h1>ToDo List</h1>
    <input onChange={inputevent} type="text" placeholder="type here" value={name}/><button onClick={todolist} className="add">+</button>
    <ul>
    {list.map((itemvlaue,index)=>{
      return (<>
      <li><button onclick={()=>deltodolist(index)}className="del">x</button>{itemvlaue}</li></>);
    })}
    </ul>
      </div></div></>);
}
export default App;
