import React, { useState } from "react";
const App=()=>{
  const state=useState();
  const[name,setname]=useState("");
  const[list,setlist]=useState([]);
  const inputevent=(event)=>{
    setname(event.target.value);
  }
  const todolist=()=>{
    setlist((prev)=>{
      return[...prev,name];
    });
    setname("");
  }
  return(<><div className="main">
    <div className="center"><h1>ToDo List</h1>
    <input onChange={inputevent} type="text" placeholder="type here" value='name'/><button onClick={todolist}>+</button>
    <ul>
    {list.map((itemvlaue)=>{
      return <li>{itemvlaue}</li>
    })}
    </ul>
      </div></div></>);
}
export default App;
