import React, { useState } from "react";
import "./index.css"
const state=useState();
const[name,setname]=useState({
  fname:"",
});
const inputevent=(event)=>{
setname(() => {
return(fname:event.target.value)
})
}
const App=()=>{
  return (<><h1>hello{name.fname}</h1>
  <input onChange={inputevent}/></>);
}
export default App;
